mapper.select:
SELECT version_id, version_name, code_int, version_code, downLoad_url, introduce, create_time FROM app_version WHERE 1=1
AND code_int = #{codeInt}
ORDER BY create_time DESC

SELECT top 1 versionCode, versionType, md5, downLoadUrl, introduce FROM app_version WHERE state = 1 AND isDel = 1 AND terminal = #{terminal}
AND versionId > #{versionId}
ORDER BY versionId DESC LIMIT 1
